// ‚úì CORRECT: Module with pure functions

// types.ts
type User = Readonly<{
  id: UserId
  name: string
  email: EmailAddress
}>

// createUser.ts
function createUser(id: UserId) {
  return function createUserWithId(name: string) {
    return function createUserWithIdAndName(email: EmailAddress): User {
      return {
        id,
        name,
        email
      }
    }
  }
}

// updateUser.ts
function updateUser(user: User) {
  return function updateUserWithUser(updates: Partial<User>): User {
    return { ...user, ...updates }
  }
}

// Usage
const user = createUser(userId)("Alice")(emailAddress)
const updated = updateUser(user)({ name: "Alice Smith" })

// ‚úì CORRECT: Immutable operations

// Immutable object update
function updateUserName(user: Readonly<User>) {
  return function updateUserNameWithUser(name: string): Readonly<User> {
    return { ...user, name }
  }
}

// Immutable array operations
function addItem<T>(array: ReadonlyArray<T>) {
  return function addItemToArray(item: T): ReadonlyArray<T> {
    return [...array, item]
  }
}

function removeItem<T>(array: ReadonlyArray<T>) {
  return function removeItemFromArray(index: number): ReadonlyArray<T> {
    return [...array.slice(0, index), ...array.slice(index + 1)]
  }
}

function updateItem<T>(array: ReadonlyArray<T>) {
  return function updateItemInArray(index: number) {
    return function updateItemInArrayAtIndex(item: T): ReadonlyArray<T> {
      return array.map(function mapItem(current, i) {
        return i === index ? item : current
      })
    }
  }
}

// All variables const
const original = [1, 2, 3]
const withFour = addItem(original)(4)
const withoutFirst = removeItem(original)(0)

// ‚úì CORRECT: Functional iteration

// Transform with map
function doubleNumbers(numbers: ReadonlyArray<number>): ReadonlyArray<number> {
  return numbers.map(function doubleNumber(n: number): number {
    return n * 2
  })
}

// Filter with filter
function getEvenNumbers(numbers: ReadonlyArray<number>): ReadonlyArray<number> {
  return numbers.filter(function isEven(n: number): boolean {
    return n % 2 === 0
  })
}

// Accumulate with reduce
function sumNumbers(numbers: ReadonlyArray<number>): number {
  return numbers.reduce(function addNumber(sum: number, n: number): number {
    return sum + n
  }, 0)
}

// Complex transformation with composition
function processNumbers(numbers: ReadonlyArray<number>): number {
  return pipe(
    numbers,
    filter(isEven),
    map(double),
    reduce(add, 0)
  )
}

// Recursion for custom iteration
function factorial(n: number): number {
  return n <= 1 ? 1 : n * factorial(n - 1)
}

// ‚úì CORRECT: Error as value with Result

type DbError = { _tag: 'DbError', message: string }
type ValidationError = { _tag: 'ValidationError', field: string, message: string }

// Sync operation returning Result
function parseUserId(input: string): Result<UserId, ValidationError> {
  if (isEmpty(input)) {
    return error({
      _tag: 'ValidationError',
      field: 'userId',
      message: 'User ID cannot be empty'
    })
  }
  return ok(input as UserId)
}

// Async operation returning Promise<Result>
function fetchUser(id: UserId): Promise<Result<User, DbError>> {
  return fetch(`/api/users/${unwrapUserId(id)}`)
    .then(function handleResponse(response) {
      return response.ok
        ? response.json().then(ok)
        : error({ _tag: 'DbError', message: `HTTP ${response.status}` })
    })
    .catch(function handleError(err) {
      return error({
        _tag: 'DbError',
        message: err instanceof Error ? err.message : 'Unknown error'
      })
    })
}

// Validation with error accumulation
function validateUser(data: unknown): Validation<User, ValidationError> {
  const errors: Array<ValidationError> = []

  if (!hasProperty('name')(data) || isEmpty(data.name)) {
    errors.push({ _tag: 'ValidationError', field: 'name', message: 'Name required' })
  }

  if (!hasProperty('email')(data) || !isValidEmail(data.email)) {
    errors.push({ _tag: 'ValidationError', field: 'email', message: 'Valid email required' })
  }

  return isEmpty(errors)
    ? success(data as User)
    : failure(errors)
}

functional_programming_rules.pattern:
// ‚úì CORRECT: Pure functions

// Pure: same input always gives same output
function add(a: number) {
  return function addToA(b: number): number {
    return a + b
  }
}

// Pure: no side effects, returns new value
function updateUserEmail(user: Readonly<User>) {
  return function updateUserEmailWithUser(email: EmailAddress): Readonly<User> {
    return { ...user, email }
  }
}

// Pure: no external dependencies
function calculateTotal(items: ReadonlyArray<number>): number {
  return items.reduce(function sumItem(total: number, item: number): number {
    return total + item
  }, 0)
}

// Pure composition
function processData(data: ReadonlyArray<number>): number {
  return pipe(
    data,
    map(double),
    filter(isPositive),
    reduce(add, 0)
  )
}

// ‚úì CORRECT: Immutable data structures

// Readonly types
type User = Readonly<{
  id: UserId
  name: string
  email: EmailAddress
  roles: ReadonlyArray<Role>
}>

// Immutable update patterns
function updateUser(user: User) {
  return function updateUserWithUser(updates: Partial<User>): User {
    return { ...user, ...updates }
  }
}

function addRole(user: User) {
  return function addRoleToUser(role: Role): User {
    return {
      ...user,
      roles: [...user.roles, role]
    }
  }
}

function removeRole(user: User) {
  return function removeRoleFromUser(roleId: RoleId): User {
    return {
      ...user,
      roles: user.roles.filter(function keepRole(role: Role): boolean {
        return role.id !== roleId
      })
    }
  }
}

// Nested immutable updates
function updateUserAddress(user: User) {
  return function updateUserAddressWithUser(street: string): User {
    return {
      ...user,
      address: {
        ...user.address,
        street
      }
    }
  }
}

// ‚úì CORRECT: Total functions with explicit types

// Maybe for optional values
type Maybe<T> = 
  | { _tag: 'just', value: T }
  | { _tag: 'nothing' }

function findUser(users: ReadonlyArray<User>) {
  return function findUserInUsers(id: UserId): Maybe<User> {
    const found = users.find(function matchesId(user: User): boolean {
      return user.id === id
    })
    return found ? just(found) : nothing()
  }
}

// Result for operations that can fail
function parseNumber(input: string): Result<number, ParseError> {
  const num = Number(input)
  return isNaN(num)
    ? error({ _tag: 'ParseError', message: 'Invalid number' })
    : ok(num)
}

// Validation for accumulating errors
function validateForm(data: FormData): Validation<ValidForm, ValidationError> {
  const nameResult = validateName(data.name)
  const emailResult = validateEmail(data.email)
  const ageResult = validateAge(data.age)

  return combineValidations({
    name: nameResult,
    email: emailResult,
    age: ageResult
  })
}

// Handle all cases explicitly
function getUserName(maybeUser: Maybe<User>): string {
  return matchMaybe(
    function handleJust(user: User): string {
      return user.name
    },
    function handleNothing(): string {
      return 'Anonymous'
    }
  )(maybeUser)
}

// ‚úì CORRECT: Function composition

// Simple composition with pipe (left-to-right)
function processUser(user: User): string {
  return pipe(
    user,
    getUserEmail,
    normalizeEmail,
    formatForDisplay
  )
}

// Composition with compose (right-to-left)
const processUserName = compose(
  formatForDisplay,
  normalizeEmail,
  getUserEmail
)

// Building complex from simple
function double(n: number): number {
  return n * 2
}

function increment(n: number): number {
  return n + 1
}

function square(n: number): number {
  return n * n
}

const doubleIncrementSquare = compose(
  square,
  increment,
  double
)

// Curried functions for composition
function map<A, B>(f: (a: A) => B) {
  return function mapWithF(array: ReadonlyArray<A>): ReadonlyArray<B> {
    return array.map(f)
  }
}

function filter<A>(predicate: (a: A) => boolean) {
  return function filterWithPredicate(array: ReadonlyArray<A>): ReadonlyArray<A> {
    return array.filter(predicate)
  }
}

const processNumbers = compose(
  reduce(add, 0),
  map(double),
  filter(isEven)
)

// ‚úì CORRECT: Higher-order functions

// Function taking function as parameter
function map<A, B>(f: (a: A) => B) {
  return function mapWithF(array: ReadonlyArray<A>): ReadonlyArray<B> {
    return array.map(f)
  }
}

// Function returning function
function greaterThan(threshold: number) {
  return function isGreaterThanThreshold(value: number): boolean {
    return value > threshold
  }
}

// Combining HOFs
function filterMap<A, B>(predicate: (a: A) => boolean) {
  return function filterMapWithPredicate(f: (a: A) => B) {
    return function filterMapWithPredicateAndF(array: ReadonlyArray<A>): ReadonlyArray<B> {
      return array.filter(predicate).map(f)
    }
  }
}

// Practical example
const getAdultNames = filterMap(
  function isAdult(user: User): boolean {
    return user.age >= 18
  }
)(
  function getName(user: User): string {
    return user.name
  }
)

// Partial application
const isPositive = greaterThan(0)
const isAdult = greaterThan(17)

const positiveNumbers = numbers.filter(isPositive)
const adults = users.filter(function filterAdult(user: User): boolean {
  return isAdult(user.age)
})

