// Practical example
function filterByProperty<T, K extends keyof T>(property: K) {
  return function filterByPropertyKey(value: T[K]) {
    return function filterByPropertyKeyAndValue(array: ReadonlyArray<T>): ReadonlyArray<T> {
      return array.filter(function matchesPropertyValue(item: T): boolean {
        return item[property] === value
      })
    }
  }
}

const filterByStatus = filterByProperty<User, 'status'>('status')
const filterByActiveStatus = filterByStatus('active')
