typescript_rules.pattern:
// ‚úì CORRECT: Discriminated unions with _tag

// Result type
type Result<T, E> =
	| { _tag: 'ok', value: T }
	| { _tag: 'error', error: E }

// Validation type
type Validation<T, E> =
	| { _tag: 'success', value: T }
	| { _tag: 'failure', errors: ReadonlyArray<E> }

// Maybe type
type Maybe<T> =
	| { _tag: 'just', value: T }
	| { _tag: 'nothing' }

// Custom domain type
type PaymentStatus =
	| { _tag: 'pending', orderId: OrderId }
	| { _tag: 'processing', transactionId: TransactionId }
	| { _tag: 'completed', receiptId: ReceiptId }
	| { _tag: 'failed', error: PaymentError }

// Exhaustive pattern matching
function handlePaymentStatus(status: PaymentStatus): string {
	switch (status._tag) {
		case 'pending':
			return `Order ${status.orderId} is pending`
		case 'processing':
			return `Transaction ${status.transactionId} processing`
		case 'completed':
			return `Receipt ${status.receiptId} available`
		case 'failed':
			return `Payment failed: ${status.error}`
	}
}

// ‚úì CORRECT: Branded types with smart constructors

// Brand definition
type UserId = string & { readonly __brand: 'UserId' }
type EmailAddress = string & { readonly __brand: 'EmailAddress' }
type PositiveNumber = number & { readonly __brand: 'PositiveNumber' }

// Smart constructor (validates)
function userId(str: string): Result<UserId, ValidationError> {
	if (isEmpty(str)) {
		return error({
			_tag: 'ValidationError',
			field: 'userId',
			message: 'User ID cannot be empty'
		})
	}
	return ok(str as UserId)
}

// Unsafe constructor (for trusted sources)
function unsafeUserId(str: string): UserId {
	return str as UserId
}

// Unwrap to raw value
function unwrapUserId(id: UserId): string {
	return id as string
}

// Usage
const idResult = userId('user123')
const id = matchResult(
	function handleOk(id: UserId) {
		return id
	},
	function handleError(err: ValidationError) {
		throw new Error(err.message)
	}
)(idResult)

// From database (already validated)
const dbId = unsafeUserId(row.id)

// To API (extract raw value)
fetch(`/api/users/${unwrapUserId(id)}`)

// ‚úì CORRECT: Explicit type annotations

// Function parameters and return type
function calculateTotal(
	items: ReadonlyArray<Item>,
	taxRate: number
): number {
	const subtotal = items.reduce(
		function sumItemPrice(total: number, item: Item): number {
			return total + item.price
		},
		0
	)
	return subtotal * (1 + taxRate)
}

// Curried function with all annotations
function map<A, B>(f: (a: A) => B) {
	return function mapWithF(
		array: ReadonlyArray<A>
	): ReadonlyArray<B> {
		return array.map(f)
	}
}

// Type aliases for complex types
type UserData = Readonly<{
	id: UserId
	name: string
	email: EmailAddress
}>

function createUser(data: UserData): Result<User, ValidationError> {
	return validateUserData(data)
}

// Async functions
async function fetchUser(
	id: UserId
): Promise<Result<User, DbError>> {
	return fetch(`/api/users/${unwrapUserId(id)}`)
		.then(function handleResponse(r) {
			return r.ok ? r.json().then(ok) : error(dbError)
		})
}

// ‚úì CORRECT: Readonly types everywhere

// Readonly object types
type User = Readonly<{
	id: UserId
	name: string
	email: EmailAddress
	roles: ReadonlyArray<Role>
}>

// ReadonlyArray for arrays
function processUsers(
	users: ReadonlyArray<User>
): ReadonlyArray<string> {
	return users.map(function getUserName(user: User): string {
		return user.name
	})
}

// Readonly parameters
function updateUser(
	user: Readonly<User>,
	updates: Readonly<Partial<User>>
): Readonly<User> {
	return { ...user, ...updates }
}

// Nested readonly
type Config = Readonly<{
	database: Readonly<{
		host: string
		port: number
		credentials: Readonly<{
			username: string
			password: string
		}>
	}>
	api: Readonly<{
		endpoint: string
		timeout: number
	}>
}>

// Readonly in generics
function first<T>(
	array: ReadonlyArray<T>
): Maybe<T> {
	return array.length > 0
		? just(array[0])
		: nothing()
}

// ‚úì CORRECT: Type-level programming

// Mapped types
type Optional<T> = {
	[K in keyof T]?: T[K]
}

type DeepReadonly<T> = {
	readonly [K in keyof T]: T[K] extends object
		? DeepReadonly<T[K]>
		: T[K]
}

// Conditional types
type Unwrap<T> = T extends Promise<infer U> ? U : T
type ArrayElement<T> = T extends ReadonlyArray<infer E> ? E : never

// Template literal types
type EventName = `on${Capitalize<string>}`
type HttpMethod = 'GET' | 'POST' | 'PUT' | 'DELETE'
type ApiRoute = `/${string}`

// Type constraints
function getProperty<T, K extends keyof T>(
	obj: T,
	key: K
): T[K] {
	return obj[key]
}

// Utility types
type NonNullableFields<T> = {
	[K in keyof T]: NonNullable<T[K]>
}

type RequiredFields<T, K extends keyof T> = T & {
	[P in K]-?: T[P]
}

// Practical example
type ApiResponse<T> = Result<T, ApiError>
type AsyncApiCall<T> = () => Promise<ApiResponse<T>>

function callApi<T>(
	apiFn: AsyncApiCall<T>
): Promise<Result<T, ApiError>> {
	return apiFn()
}
